   const debounceDelay = 1000; // 1秒

   socket.onmessage = function(event) {
       clearTimeout(updateTimeout);
       updateTimeout = setTimeout(() => {
           const stockPrice = JSON.parse(event.data);
           const currentTimeLabel = new Date().toLocaleTimeString();

           stockData.labels.push(currentTimeLabel);
           stockData.datasets[0].data.push(stockPrice);

