    bool inQuotes = false;
    char prevChar = '\0';
    std::string value;

    while (ss.get(*cell.rbegin())) {
        if (cell.back() == '"' && (prevChar != '\\' || prevChar == '\0')) {
            inQuotes = !inQuotes;
        } else if (cell.back() == ',' && !inQuotes) {
            result.push_back(value);
            value.clear();
        } else {
            value += cell.back();
        }
        prevChar = cell.back();
    }
    if (!value.empty()) {
        result.push_back(value);
    }
    return result;
}

void parseCSV(const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Unable to open file\n";
        return;
    }

    std::string line;
    std::vector<ColumnStats> stats;
    bool firstLine = true;

    while (std::getline(file, line)) {
        std::vector<std::string> row = parseCSVLine(line);
        int colIndex = 0;

        for (const auto& cell : row) {
            double value = std::stod(cell);
            if (firstLine) {
                stats.push_back({value, value, value, 1});
            } else {
                if (value > stats[colIndex].max) stats[colIndex].max = value;
                if (value < stats[colIndex].min) stats[colIndex].min = value;
                stats[colIndex].sum += value;
                stats[colIndex].count++;
            }
            colIndex++;
        }
        firstLine = false;
    }

    file.close();

    for (const auto& stat : stats) {
        std::cout << "Max: " << stat.max << ", Min: " << stat.min 
                  << ", Avg: " << stat.sum / stat.count << '\n';
    }
}

